mapper.update:
UPDATE fundingqna
	SET
		qnaFdCode=#{qnaFdCode},
		qnaFdTitle=#{qnaFdTitle},
		qnaFdContent=#{qnaFdContent}
WHERE qnaCode=#{qnaCode}

UPDATE fundingqnareply
	SET
		qnaReContent=#{qnaReContent}
WHERE qnaReCode = #{qnaReCode}

mapper.select:
SELECT f.fdCode, f.fdTitle, f.numberOfShares, c.comName, CASE f.fdStatus WHEN '모집중' THEN (SELECT SUM(si.purchaseShares) FROM investment si WHERE si.investFdCode = f.fdCode GROUP BY si.investFdCode ) WHEN '결제모집중' THEN (SELECT SUM(si.purchaseShares) FROM investment si WHERE si.investFdCode = f.fdCode AND si.payCheck = 1 GROUP BY si.investFdCode) WHEN '진행중' THEN (SELECT SUM(si.purchaseShares) FROM investment si WHERE si.investFdCode = f.fdCode AND si.payCheck = 1 GROUP BY si.investFdCode) WHEN '모집실패' THEN(SELECT SUM(si.purchaseShares) FROM investment si WHERE si.investFdCode = f.fdCode AND si.payCheck = 1 GROUP BY si.investFdCode) END as total, f.minInvestMoney, f.issuePrice, f.fdStatus, TO_DAYS(f.projectStartDate)-TO_DAYS(now()) as days, (SELECT mf.flowCode FROM moneyflow mf WHERE f.fdCode = mf.mfFdCode AND mf.mfCategory = 3) checkassignment FROM funding f LEFT JOIN company c ON f.fdComCode = c.comCode WHERE f.fdStatus = '결제모집중' or f.fdStatus = '진행중' or (f.fdStatus = '모집실패' AND (SELECT SUM(ssi.purchaseShares) FROM investment ssi WHERE ssi.investFdCode = f.fdCode AND ssi.payCheck = 1)) GROUP BY f.fdCode ORDER BY f.fdDate DESC
limit #{numberOfRequests},10;
limit 0,10;

SELECT *
FROM investment
WHERE investFdCode=#{fdCode} AND payCheck = 1

	limit 0,10;


	limit #{numberOfRequests},10;
