mapper.select:
SELECT t.tlCode, t.tlTitle, t.tlContent, t.tlTime, u.nickname, e.emRankCode, e.emComName, (SELECT COUNT(tlCode) FROM timelinelike WHERE tlCode = t.tlCode) as timelinelikecount, tl.tlLikeId, (SELECT nickname FROM user su WHERE tl.tlLikeId = su.userId) as timelinelikenickname FROM timeline t LEFT JOIN user u ON t.tlId = u.userId LEFT JOIN employee e ON u.userId = e.emUserId LEFT JOIN timelinelike tl ON tl.tlCode = t.tlCode ORDER BY t.tlTime DESC
limit #{numberOfRequests},10;
limit #{numberOfRequests},10;

mapper.update:
UPDATE timeline
	SET
		tlTitle=#{tlTitle},
		tlContent=#{tlContent}
WHERE tlCode = #{tlCode}

UPDATE timelinereply
	SET
		tlReContent=#{tlReContent}
WHERE tlReCode=#{tlReCode}

mapper.select:
SELECT t.tlCode, t.tlTitle, t.tlContent, t.tlTime, u.nickname, e.emRankCode, e.emComName, (SELECT COUNT(tlCode) FROM timelinelike WHERE tlCode = t.tlCode) as timelinelikecount, tl.tlLikeId, (SELECT nickname FROM user su WHERE tl.tlLikeId = su.userId) as timelinelikenickname FROM timeline t LEFT JOIN user u ON t.tlId = u.userId LEFT JOIN employee e ON u.userId = e.emUserId LEFT JOIN timelinelike tl ON tl.tlCode = t.tlCode ORDER BY (SELECT COUNT(str.tlReCode) FROM timelinereply str WHERE str.tlCode = t.tlCode ) DESC
limit #{numberOfRequests},10;
limit #{numberOfRequests},10;

SELECT t.tlCode, t.tlTitle, t.tlContent, t.tlTime, u.nickname, e.emRankCode, e.emComName, (SELECT COUNT(tlCode) FROM timelinelike WHERE tlCode = t.tlCode) as timelinelikecount, tl.tlLikeId, (SELECT nickname FROM user su WHERE tl.tlLikeId = su.userId) as timelinelikenickname FROM timeline t LEFT JOIN user u ON t.tlId = u.userId LEFT JOIN employee e ON u.userId = e.emUserId LEFT JOIN timelinelike tl ON tl.tlCode = t.tlCode ORDER BY (SELECT COUNT(stl.tlCode) FROM timelinelike stl WHERE stl.tlCode = t.tlCode ) DESC
limit #{numberOfRequests},10;
limit #{numberOfRequests},10;

SELECT mf.flowCode,
	   mf.mfFdCode,
	   mf.mfInvestCode,
	   mf.mfAmount,
	   mf.mfCategory,
	   mf.mfDate,
	   (SELECT fdTitle FROM funding WHERE mf.mfFdCode = fdCode) title,
	   (SELECT investId FROM investment WHERE mf.mfInvestCode = investCode ) id 
FROM moneyflow mf



		ORDER BY mf.mfDate DESC


				limit #{numberOfRequests},10;


				limit 0,10;




		WHERE mf.mfCategory = #{mfCategory}
		ORDER BY mf.mfDate DESC


				limit #{numberOfRequests},10;


				limit 0,10;

SELECT * FROM companyreport cr 


		WHERE cr.reportReceiveId = #{id}


		WHERE cr.reportSendId = #{id}


ORDER BY cr.reportTime DESC


		limit #{numberOfRequests},10;


		limit 0,10;
