mapper.select:
SELECT b.areaCode, b.areaName, f.fdTitle, c.comName FROM businessarea b INNER JOIN funding f ON b.areaFdCode = f.fdCode INNER JOIN company c ON f.fdComCode = c.comCode c.comName in
#{comList.emComName}
ORDER BY c.comName, b.areaCode asc

mapper.update:
UPDATE funding
SET
	adminApproval=1,
	fdApprovalDate=NOW(),
	fdApprovalId=#{fdApprovalId}
WHERE fdCode=#{fdCode}

mapper.select:
		SELECT accountCode, accountId, secCompany, accountNumber, accountNickname
 		FROM account 
 		WHERE accountCode=#{accountCode}

SELECT comName
FROM company
WHERE comName=#{comName}

SELECT *
FROM employee
WHERE emUserId=#{emUserId}

SELECT *
FROM company
WHERE comOpenUserId=#{comOpenUserId}

SELECT *
FROM company
WHERE comCode=#{comCode}

SELECT * FROM employee emComName in
#{comList.emComName}
ORDER BY emComName, emCode asc

SELECT *
FROM employee
WHERE emCode=#{emCode}

SELECT *
FROM employee
WHERE emComCode=#{emComCode}

mapper.update:
UPDATE company
SET
comAdminApproval=#{comAdminApproval},
comApprovalDate=NOW(),
comApprovalId=#{comApprovalId}
WHERE comCode=#{comCode}

UPDATE company
SET
comDelApprovalId=#{comDelApprovalId}
WHERE comCode=#{comCode}

mapper.select:
SELECT comCode, comName
FROM company
WHERE comName=#{comName}

SELECT userDetailId, phoneFront3, phoneRest8, postalCode, address, address2, nameCheck
FROM userdetail 
WHERE userDetailId=#{userDetailId}
